mapper.select:
SELECT

FROM SYS_PERMISSION

	
		
			ID=#{id}
		
		
			AND PERMIS_TYPE=#{permisType}
		
		
			AND PERMIS_NAME=#{permisName}
		
		
			AND PERMIS_DESC=#{permisDesc}
		
		
			AND PERMIS_CODE=#{permisCode}
		
		
			AND MENU_ID=#{menuId}
		
		
			AND STATUS=#{status}
		
		
			AND CREATOR_DATE=#{creatorDate}
		
		
			AND CREATOR_ID=#{creatorId}
		
		
			AND UPDATE_ID=#{updateId}
		
		
			AND UPDATE_DATE=#{updateDate}


	SELECT D.* FROM (SELECT T.*, ROWNUM RN FROM (

SELECT

FROM SYS_PERMISSION A

	
		
			AND A.PERMIS_NAME LIKE '%${permisName}%'
		
		
			AND A.PERMIS_CODE LIKE '%${permisCode}%'
		
		
			AND A.STATUS = #{status}
		
		
			AND A.PERMIS_TYPE = #{permisType}
		
		
			AND A.ID = #{id}
		
	


 ) T WHERE ROWNUM <=#{page}*#{rows} ) D WHERE D.RN > ((#{page}-1)*#{rows})


SELECT COUNT(1) FROM
		(SELECT ID FROM SYS_PERMISSION A

	
		
			
				AND A.PERMIS_NAME LIKE '%${permisName}%'
			
			
				AND A.PERMIS_CODE LIKE '%${permisCode}%'
			
			
				AND A.STATUS = #{status}
			
			
				AND A.PERMIS_TYPE = #{permisType}
			
			
				AND A.ID = #{id}
			
		
	
	) T

	
	SELECT D.* FROM (SELECT T.*, ROWNUM RN FROM (
	
    SELECT A.* FROM SYS_PERMISSION A
     INNER JOIN SYS_ROLE_PERMISSION B
         ON A.ID=B.PERMIS_ID
		
			
				
					AND B.STATUS =1
				
				
					AND A.PERMIS_NAME LIKE '%${permisName}%'
				
				
					AND A.PERMIS_CODE LIKE '%${permisCode}%'
				
				
					AND A.STATUS = #{status}
				
				
					AND A.PERMIS_TYPE = #{permisType}
				
				
				    AND B.ROLE_ID IN
                    
						#{role.id}
					
			    
				
					AND A.ID = #{id}
				
			
		
		
		 ) T WHERE ROWNUM <=#{page}*#{rows} ) D WHERE D.RN > ((#{page}-1)*#{rows})

	
	SELECT COUNT(1) FROM
			(SELECT A.* FROM SYS_PERMISSION A
			     INNER JOIN SYS_ROLE_PERMISSION B
			         ON A.ID=B.PERMIS_ID
	
		
			
			
				
					AND B.STATUS =1
				
				
					AND A.PERMIS_NAME LIKE '%${permisName}%'
				
				
					AND A.PERMIS_CODE LIKE '%${permisCode}%'
				
				
					AND A.STATUS = #{status}
				
				
					AND A.PERMIS_TYPE = #{permisType}
				
				
				    AND B.ROLE_ID IN
                    
						#{role.id}
					
			    
				
					AND A.ID = #{id}
				
			
		
		) T

	
	SELECT D.* FROM (SELECT T.*, ROWNUM RN FROM (
	
    SELECT A.* FROM SYS_PERMISSION A
		
			
				
					AND A.PERMIS_NAME LIKE '%${permisName}%'
				
				
					AND A.PERMIS_CODE LIKE '%${permisCode}%'
				
				
					AND A.STATUS = #{status}
				
				
					AND A.PERMIS_TYPE = #{permisType}
				
				
				    AND A.ID NOT IN(SELECT RP.PERMIS_ID
                      FROM SYS_ROLE_PERMISSION RP
                     WHERE RP.ROLE_ID IN
                     
						#{role.id}
					
					 AND RP.STATUS = 1
                     ) 
			    
				
					AND A.ID = #{id}
				
			
		
		
		 ) T WHERE ROWNUM <=#{page}*#{rows} ) D WHERE D.RN > ((#{page}-1)*#{rows})

	
	SELECT COUNT(1) FROM
			(SELECT A.* FROM SYS_PERMISSION A
	
		
			
			
				
					AND A.PERMIS_NAME LIKE '%${permisName}%'
				
				
					AND A.PERMIS_CODE LIKE '%${permisCode}%'
				
				
					AND A.STATUS = #{status}
				
				
					AND A.PERMIS_TYPE = #{permisType}
				
				
				    AND A.ID NOT IN(SELECT RP.PERMIS_ID
                      FROM SYS_ROLE_PERMISSION RP
                     WHERE RP.ROLE_ID IN
                     
						#{role.id}
					
					 AND RP.STATUS = 1
                     ) 
			    
				
					AND A.ID = #{id}
				
			
		
		) T
